'use strict';
module.exports = function(id, type) {
    //Note that the src is set to an empty html page to avoid browser spasms in Chrome and perhaps others.
    if (type == null) {
        type = 'youtube';
    }
    var output  = '';
    output += '<div class="video_wrapper_outer">';
    output += '<div class="video_wrapper">';
    if (type == 'youtube') {
        output += '<iframe class="lazy" width="580" height="326" src="/ajax_html/site/loading.html" data-src="//www.youtube.com/embed/'+id+'" frameborder="0" allowfullscreen></iframe>';
    }
    if (type == 'vimeo') {
        output += '<iframe class="lazy" src="/ajax_html/site/loading.html" data-src="//player.vimeo.com/video/'+id+'?badge=0" width="500" height="281" frameborder="0" webkitallowfullscreen mozallowfullscreen allowfullscreen></iframe>';
    }

    //http://dai.ly/x1ub7t6
    if (type == 'dailymotion') {
        output += '<iframe class="lazy" src="/ajax_html/site/loading.html" data-src="//www.dailymotion.com/embed/video/'+id+'" width="480" height="270" frameborder="0" webkitallowfullscreen mozallowfullscreen allowfullscreen></iframe>';
    }
    output += '</div>';
    output += '</div>';
    return output;
}
